import React, { useState, useRef } from 'react'
import "./Video.css";

function Video() {
    const [play, setPlay] = useState(true);
    const vidRef = useRef(null);


    const handleVid = () => {
        if (play) {
            vidRef.current.pause();
            setPlay(false);
        }
        else {
            vidRef.current.play();
            setPlay(true);
        }
    }
    return (
        <div className="video">

            <video
                className="videoPlayer"
                onClick={handleVid}
                loop
                ref={vidRef}
                autoPlay
                src="https://v16-web.tiktok.com/video/tos/useast2a/tos-useast2a-ve-0068c004/fef396f8a8a14154976bfeb400427bd6/?a=1988&br=2484&bt=1242&cd=0%7C0%7C1&ch=0&cr=0&cs=0&cv=1&dr=0&ds=3&er=&expire=1630601051&ft=9wMeReta4kag3&l=202109021044010101890651580B0800A0&lr=tiktok_m&mime_type=video_mp4&net=0&pl=0&policy=3&qs=0&rc=M2l5aDg6ZjNrNzMzNzczM0ApNjY4N2Q3M2U2NzVnPDo1ZmcuXl9lcjRfMzBgLS1kMTZzc18zNjRhXi1gMzEuLS8uYDM6Yw%3D%3D&signature=cd25ec9dbf20be72dd042114b03125d5&tk=7002315202755478533&vl=&vr="
                type="video/mp4"
            >
            </video>
        </div>
    )
}

export default Video;
